/* css rest */
* {
    box-sizing: border-box;
}

*:before, * after {
    box-sizing: border-box;
}

* {
    margin: 0;
    padding: 0;
}

ul, ol {
    list-style: none;
}

a {
    color: inherit;
    text-decoration: none;
}

img {
    max-width: 100%;
    max-height: 100%;
}

input {
    outline: none;
}

body {
    background-image: url("ef76f954e1b3252ce0a2f099c9e1f649.jpg");
    background-repeat: no-repeat;
}

/* style */

.globalHeader {
    margin: 0;
    display: flex;
    justify-content: center;
    align-items: center;
}

.clock {
    position: fixed;
    width: 100px;
    height: 100px;
    margin-left: auto;
    text-align: center;
}

#time {
    margin-top: 160px;
    color: #fff;
    font-weight: normal;
}

input::-webkit-input-placeholder { /*搜索框文本颜色*/
    color: #fff;
    border: none;
}

.siteList {
    display: flex;
    margin-top: 230px;
    flex-wrap: wrap;
    justify-content: center;
}

@media (min-width: 500px) {
    .siteList {
        margin: 230px;
        margin-left: 0;
        margin-right: 0;
        justify-content: center;
    }
}

.siteList > li {
    display: flex;
    margin: 20px;
    margin-left: 5px;
}

@media (min-width: 500px) {
    .siteList > li {
        display: flex;
        margin: 20px;
        margin-left: 5px;
        margin-right: 5px;
    }
}

.siteList .addButton {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    width: 80px;
    height: 80px;
    background-color: rgba(255, 255, 255, .25);
    border-radius: 4px;
}

.hover-zoom:hover {
    width: 900px;
    background-color: white;
}

.siteList .addButton .icon {
    width: 40px;
    height: 40px;
    display: flex;
    justify-content: center;
    align-items: center;
}

.siteList .addButton .text {
    color: white;
    font-size: 10px;
}

.icon-wrapper {
    width: 60px;
    height: 60px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 45px;

}

.globalMain .site {
    width: 80px;
    height: 80px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    background-color: rgba(255, 255, 255, .25);
    margin-bottom: 10px;
    border-radius: 4px;
    padding: 20px 0;
    position: relative;
    cursor: pointer;
}

.globalMain .site .close {
    position: absolute;
    right: 3px;
    top: 3px;
    display: none;
    cursor: default;
}

.globalMain .site:hover > .close {
    display: block;
}

.globalMain .site > .logo {
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 45px;
    color: white;
    text-transform: uppercase;
}

.globalMain .site > .link {
    color: white;
    font-size: 10px;
}

.searchFrom {
    display: flex;
    justify-content: space-between;
    max-width: 400px;
}

.globalMain {
    max-width: 500px;
    margin-left: auto;
    margin-right: auto;
}

@media (min-width: 500px) {
    .searchFrom {
        max-width: 400px;
        margin-left: auto;
        margin-right: auto;
    }

    .searchFrom > input:hover {
        width: 400px;
        border-radius: 30px;
        background: rgba(255, 255, 255, 0.5);
        color: red;
        border: none;
    }
}

.searchFrom > input {
    position: absolute;
    top: 180px;
    left: 50%;
    transform: translateX(-50%);
    width: 230px;
    max-width: 80%;
    /*outline: 0;*/
    border: none;
    padding: 8px 1px;
    border-radius: 30px;
    font-family: "Microsoft Yahei", sans-serif;
    text-align: center;
    background-color: rgba(255, 255, 255, .25);
    box-shadow: rgba(0 0 0 / 20%) 0 0 10px;
    -webkit-backdrop-filter: blur(10px);
    backdrop-filter: blur(10px);
}
